import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: props => 
                  props.widthInput? 400: 200,
        backgroundColor: "white",
        borderRadius: "2px",
    },
    selectBox: {
      paddingLeft: "20px"
    }
}));
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};




export default function SearchDropDown (props){
 const { MakesList, getMakeSelection, labelTitle, widthInput, item} = props;
    const classes = useStyles(props)
    const [itemMake, setItemMake] = React.useState({
      "make": item? item.make:""
    });
    const handleChange = (event) => {
        setItemMake({...itemMake, make:event.target.value});
      };
      React.useEffect(() => {
        getMakeSelection(itemMake.make)
      }, [itemMake.make])
    
    return(
                <FormControl className={classes.formControl}>
                <InputLabel className={classes.selectBox} >{labelTitle? labelTitle : "EnterValue"}</InputLabel>
                    <Select
                        value={itemMake.make}
                        onChange={handleChange}
                        MenuProps={MenuProps}
                        varient="outlined"
                        >
                        {MakesList.map(item =>(
                            <MenuItem key={item} value={item}>
                            {item}
                          </MenuItem>
                        ))}
                    </Select>
                </FormControl>
    )
}
