import React from 'react'
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';

import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

import HighlightsPanel from '../../../Components/HighlightsPanel/highlightsPanel'

const useStyles = makeStyles(theme => ({
    root: {
        paddingTop: "15vh",
        minHeight: "100vh",
        paddingLeft: "10vh",
        paddingRight: "10vh",
        backgroundColor: "#eee"

    },
    gridList:{
        maxWidth: 930,
        maxHeight: "100%",

    },
    sliderDiv:{
        maxWidth: 930,
        maxHeight: "100%"
    },
      mainImage:{
        width: "100%",
        height: "100%",
        objectFit: "cover"
      },
      paperHeading:{
      }
}))

function ViewUserSingleAd (props) {
    const { carItem, img } = props;
    const classes = useStyles()
 
    return (
        <div className={classes.root}>
           <Grid container
                direction="row"
                justify="space-around"
                alignItems="center">
                <Grid 
                    item
                    xs={12} 
                    className={classes.gridList} >
                        <Paper className={classes.paperHeading} elevation={3}>
                            <h1>
                            <span>{carItem.year}</span>
                            <span>   {carItem.make}</span>
                            <span>   {carItem.model}</span>

                            </h1>
                                
                        </Paper>
                        <Slider 
                            duration={1500}
                            >
                            {img.map((img,i) => (
                                <div className={classes.sliderDiv}>
                                    <img className={classes.mainImage} src={img} alt="image"/>
                                </div>
                            ))}
                        </Slider>
                         <Paper>
                             <List>
                                 <ListItem>
                                     <ListItemText
                                        primary="Category"
                                        secondary={carItem.category}
                                    />
                                 </ListItem>
                                 <Divider/>
                                 <ListItem>
                                     <ListItemText
                                        primary="Transmission"
                                        secondary={carItem.transmission}
                                    />
                                 </ListItem>
                                 <Divider/>
                                 <ListItem>
                                     {carItem.milage}
                                     <ListItemText
                                        primary="Milage"
                                        secondary={carItem.milage}
                                    />
                                 </ListItem>
                                 <Divider/>
                                 <ListItem>
                                     {carItem.fueltype}
                                     <ListItemText
                                        primary="Fuel Type"
                                        secondary={carItem.fueltype}
                                    />
                                 </ListItem>
                             </List>
                        </Paper>       
               </Grid>
               <Grid 
                    item
                    xs={12}
                    alignItems="center" >
                </Grid>
           </Grid>

        </div>
    )
}

export default ViewUserSingleAd;
