import { ADD_NEW_AD } from '../actiontypes';
const DEF_STATE = {
  "year": "",
  "make": "",
  "model": "",
  "category": "",
  "user": "",
  "milage": "",
  "transmission": "",
  "fueltype": "",
  "doors": "",
  "engine": "",
  "color": "",
  "price": "",
  "image": [],
  "description": ""
};
export default ((state = DEF_STATE, action) => {
  switch (action.type) {
    case ADD_NEW_AD:
      return {
        year: action.year,
        make: action.make,
        model: action.model,
        category: action.category,
        user: action.user,
        milage: action.milage,
        transmission: action.transmission,
        fueltype: action.fueltype,
        doors: action.doors,
        engine: action.engine,
        color: action.color,
        price: action.price,
        image: action.image,
        description: action.description
      };

    default:
      return state;
  }
});
